package com.coolxtc.common.activityimport android.app.Activityimport android.net.Uriimport android.os.Bundleimport android.text.TextUtilsimport com.coolxtc.common.Constantimport com.coolxtc.common.router.Routerimport com.coolxtc.common.router.WebSchemeRedirectimport com.coolxtc.common.util.ActivityUtil.getActivityCount/** * * * @author Administrator * @page 推送消息跳转中间页 * * * $Id$ */class BlankActivity : Activity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        val bundle = this.intent.extras        val intent = this.intent        var uri = intent.data        if (uri == null) {            //从通知栏进入, PUSH            if (bundle != null) {                var message = bundle.getString(Constant.PUSH_URL_STR)                if (TextUtils.isEmpty(message)) {                    message = bundle.getString(Constant.URL)                }                if (!TextUtils.isEmpty(message)) {                    uri = Uri.parse(message)                }            }        }        /**         * 应用没有启动，则启动应用（启动MainActivity）;         * 否则直接进入对应 Activity         */        if (uri != null) {            if (getActivityCount() <= 0) {                Router.Main.jumpWelcome(uri.path ?: "")            } else {                WebSchemeRedirect.handleWebClick(uri.path)            }            finish()        } else {            finish()        }    }}